// Config plugin to fix Firebase modular headers with New Architecture
// Adds CLANG_ALLOW_NON_MODULAR_INCLUDES_IN_FRAMEWORK_MODULES to pod targets
const { withDangerousMod } = require('@expo/config-plugins');
const fs = require('fs');
const path = require('path');

function withModularHeadersFix(config) {
  return withDangerousMod(config, [
    'ios',
    async (config) => {
      const podfilePath = path.join(config.modRequest.platformProjectRoot, 'Podfile');
      
      // Wait for Podfile to be generated by other plugins
      // This runs after expo-build-properties
      
      if (fs.existsSync(podfilePath)) {
        let podfileContents = fs.readFileSync(podfilePath, 'utf8');
        
        // Add modular headers fix code before the final 'end' of the target block
        const modularHeadersFix = `
    # Fix for Firebase modular headers with New Architecture
    # Allow non-modular includes in framework modules
    installer.pods_project.targets.each do |target|
      target.build_configurations.each do |config|
        config.build_settings['CLANG_ALLOW_NON_MODULAR_INCLUDES_IN_FRAMEWORK_MODULES'] = 'YES'
      end
    end
`;

        // Check if fix is already added
        if (!podfileContents.includes('CLANG_ALLOW_NON_MODULAR_INCLUDES_IN_FRAMEWORK_MODULES')) {
          // Find the post_install block and add our code there
          // The Expo/React Native Podfile has a post_install block already
          
          // Pattern to find post_install do |installer| and add code after it
          const postInstallPattern = /(post_install\s+do\s+\|installer\|)/;
          
          if (postInstallPattern.test(podfileContents)) {
            podfileContents = podfileContents.replace(
              postInstallPattern,
              `$1${modularHeadersFix}`
            );
          }
          
          fs.writeFileSync(podfilePath, podfileContents);
          console.log('[ios-modular-headers] Added CLANG_ALLOW_NON_MODULAR_INCLUDES_IN_FRAMEWORK_MODULES fix');
        }
      }
      
      return config;
    },
  ]);
}

module.exports = withModularHeadersFix;
